function closestmeetings(){navigator.geolocation.getCurrentPosition(show)}function toRad(t){return t*Math.PI/180}function codeIt(t){return'<td class="time">'+t.time_formatted+'</td><td class="name"><a href="'+t.url+'">'+t.name+'</a></td><td class="distance">'+t.distance+" mi</td></tr>"}function show(t){var e=jQuery("#geolocate").val();return jQuery.ajax({url:displayclosestmeetings.ajax_url,type:"GET",data:{action:"display_closest_meetings",lat:t.coords.latitude,long:t.coords.longitude,today:e},success:function(t){var e=jQuery.parseJSON(t).sort(function(t,e){var a=t.time,o=e.time;return a<o?-1:a>o?1:0});jQuery("#dataxhr").html('<thead><tr><th class="time">Time</th><th class="name">Meeting</th><th class="distance">Distance</th></tr></thead><tbody>'+codeIt(e[0])+codeIt(e[1])+codeIt(e[2])+codeIt(e[3])+"</tbody>")}}),!0}jQuery(document).ready(function(){closestmeetings()});